$(function(){var t=(new Pikaday({field:document.getElementById("start_date"),firstDay:1,minDate:moment().toDate(),format:"M/D/YYYY"}),new Pikaday({field:document.getElementById("end_date"),firstDay:1,minDate:moment().toDate(),format:"M/D/YYYY"}));$("#start_date").on("change",function(e){var a=e.currentTarget.value,n=moment(a,"M/D/YYYY"),m=n.add(2,"d");t.setMoment(moment(m,"M/D/YYYY"))})});